mapper.select:
select
wr.*, d.type, d.segment, d.stake_number, d.install_position, d.line, d.code as device_code
from tm_warning_records wr
LEFT JOIN tm_device d ON d.code = wr.deviceid

    deviceid=#{deviceid}
    and warning_level!=null
    and wr.collect_time = #{collect_time}
    and wr.collect_time >= #{timeRangeBegin}
    and #{timeRangeEnd} >= wr.collect_time
    and wr.first = #{first}

mapper.insert:
insert into tm_warning_records (deviceid, val, warning_value, 
  collect_time, warning_level, remark
  )
values (#{deviceid,jdbcType=VARCHAR}, #{val,jdbcType=DECIMAL}, #{warningValue,jdbcType=DECIMAL}, 
  #{collectTime,jdbcType=TIMESTAMP}, #{warningLevel,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )
